import { plugin, ApplyPluginsType } from '@@/core/coreExports';
// eslint-disable-next-line import/extensions
import { access as accessApi } from '../plugin-access/core';
import Exception404 from './views/404';
import Exception403 from './views/403';

if (!accessApi) {
    throw new Error(
        '[plugin-layout]: pLugin-layout depends on plugin-accessplease install plugin-access first'
    );
}

const handle = (type, router) => {
    const accesssIds = accessApi.getAccess();
    const path = `/${type}`;
    const name = `Exception${type}`;
    const components = {
        404: Exception404,
        403: Exception403
    };
    if (!accesssIds.includes(path)) {
        accessApi.setAccess(accesssIds.concat([path]));
    }
    if (!router.hasRoute(name)) {
        router.addRoute({ path, name, component: components[type] });
    }
};

export const access = memo => ({
    unAccessHandler({
        router, to, from, next
    }) {
        const runtimeConfig = plugin.applyPlugins({
            key: 'layout',
            type: ApplyPluginsType.modify,
            initialValue: {}
        });
        if (runtimeConfig.unAccessHandler && typeof runtimeConfig.unAccessHandler === 'function') {
            return runtimeConfig.unAccessHandler({
                router, to, from, next
            });
        }
        if (to.path === '/404') {
            handle(404, router);
            return next('/404');
        }
        handle(403, router);
        next('/403');
    },
    noFoundHandler({
        router, to, from, next
    }) {
        const runtimeConfig = plugin.applyPlugins({
            key: 'layout',
            type: ApplyPluginsType.modify,
            initialValue: {}
        });
        if (runtimeConfig.noFoundHandler && typeof runtimeConfig.noFoundHandler === 'function') {
            return runtimeConfig.noFoundHandler({
                router, to, from, next
            });
        }
        if (to.path === '/403') {
            handle(403, router);
            return next('/403');
        }
        handle(404, router);
        next('/404');
    },
    ...memo
});

import { plugin, ApplyPluginsType } from '@@/core/coreExports';
import { access, install } from './core';

export function onRouterCreated({ router }) {
    router.beforeEach(async (to, from, next) => {
        const runtimeConfig = plugin.applyPlugins({
            key: 'access',
            type: ApplyPluginsType.modify,
            initialValue: {}
        });
        if (to.matched.length === 0) {
            if (runtimeConfig.noFoundHandler && typeof runtimeConfig.noFoundHandler === 'function') {
                return runtimeConfig.noFoundHandler({
                    router, to, from, next
                });
            }
        }
        let path;
        if (to.matched.length === 1) {
            path = to.matched[0].path;
        } else {
            path = to.path;
        }
        const canRoute = await access.hasAccess(path);
        if (canRoute) {
            return next();
        }
        if (runtimeConfig.unAccessHandler && typeof runtimeConfig.unAccessHandler === 'function') {
            return runtimeConfig.unAccessHandler({
                router, to, from, next
            });
        }
        next(false);
    });
}

export function onAppCreated({ app }) {
    install(app);
}

import { watch } from 'vue';

const cache = new WeakMap();
const setDispaly = (el, access) => {
    if (access.value) {
        el.style.display = el._display;
    } else {
        el.style.display = 'none';
    }
};
export default function createDirective(useAccess) {
    return {
        beforeMount(el) {
            const ctx = {};
            ctx.watch = (path) => {
                el._display = el._display || el.style.display;
                const access = useAccess(path);
                setDispaly(el, access);
                return watch(access, () => {
                    setDispaly(el, access);
                });
            };
            cache.set(el, ctx);
        },
        mounted(el, binding) {
            const ctx = cache.get(el);
            if (ctx.unwatch) {
                ctx.unwatch();
            }
            ctx.unwatch = ctx.watch(binding.value);
        },
        updated(el, binding) {
            const ctx = cache.get(el);
            if (ctx.unwatch) {
                ctx.unwatch();
            }
            ctx.unwatch = ctx.watch(binding.value);
        },
        beforeUnmount(el) {
            const ctx = cache.get(el);
            if (ctx.unwatch) {
                ctx.unwatch();
            }
        }
    };
}

export default function createComponent(useAccess) {
    return (props, { slots }) => {
        const access = useAccess(props.id);
        if (!access.value || !slots.default) return null;
        return slots.default();
    };
}

